import "./App.css";
import {
  createBrowserRouter,
  createRoutesFromElements,
  Route,
  RouterProvider,
  Navigate,
} from "react-router-dom";
import Layout from "./components/Layout";
import Home from "./pages/Home";
import Cart from "./pages/Cart";
import Login from "./pages/Login";
import { Provider } from "react-redux";
import { store } from "./store";
import Checkout from "./pages/Checkout";
import AuthProvider, { useAuth } from "./firebase/Auth";
import Register from "./pages/Register";
import ProductDetail from "./pages/ProductDetail";
import AdminPage from "./pages/AdminPage";
import CustomerOrders from "./pages/Orders";
import CategoryPage from "./pages/CategoryPage";
import ForgotPassword from "./pages/ForgotPassword";
import { AdminRoute } from './firebase/AdminRoute';
import CircularProgress from "@mui/material/CircularProgress";

// Regular ProtectedRoute for non-admin protected pages
function ProtectedRoute({ children }) {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </div>
    );
  }

  if (!user || !user.emailVerified) {
    return <Navigate to="/login" />;
  }

  return children;
}

const router = createBrowserRouter(
  createRoutesFromElements(
    <Route element={<Layout />}>
      {/* Public routes */}
      <Route path="/" element={<Home />} />
      <Route path="/cart" element={<Cart />} />
      <Route path="/product/:productId" element={<ProductDetail />} />
      <Route path="/category/:categoryId" element={<CategoryPage />} />
      <Route path="/login" element={<Login />} />
      <Route path="/register" element={<Register />} />
      <Route path="/forgot-password" element={<ForgotPassword />} />

      {/* Protected user routes */}
      <Route
        path="/checkout"
        element={
          <ProtectedRoute>
            <Checkout />
          </ProtectedRoute>
        }
      />
      <Route
        path="/orders"
        element={
          <ProtectedRoute>
            <CustomerOrders />
          </ProtectedRoute>
        }
      />

      {/* Protected admin routes */}
      <Route
        path="/admin/*"
        element={
          <AdminRoute>
            <AdminPage />
          </AdminRoute>
        }
      />

      {/* Catch-all route */}
      <Route path="*" element={<Navigate to="/" replace />} />
    </Route>
  )
);

function App() {
  return (
    <Provider store={store}>
      <AuthProvider>
        <RouterProvider router={router} />
      </AuthProvider>
    </Provider>
  );
}

export default App;

import React from "react";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import { useSelector, useDispatch } from "react-redux";
import { updateAddress } from "../feature/checkout-slice";

export default function AddressForm({ onValidityChange }) {
  const address = useSelector((state) => state.checkout?.address);
  const dispatch = useDispatch();

  const requiredFields = [
    'firstName',
    'lastName',
    'address1',
    'city',
    'country',
    'phoneNumber'
  ];

  function handleChange(event) {
    const { name, value } = event.target ?? {};
    dispatch(updateAddress({ [name]: value }));

    // Check if all required fields are filled
    const isValid = requiredFields.every(field => {
      if (field === name) {
        return value.trim() !== '';
      }
      return address[field]?.trim() !== '';
    });

    onValidityChange(isValid);
  }

  return (
    <>
      <Typography variant="h6" gutterBottom>
        Shipping Address
      </Typography>
      <Box component="form" onChange={handleChange}>
        <Grid container spacing={3}>
          <Grid item xs={12} sm={6}>
            <TextField
              required
              id="firstName"
              name="firstName"
              label="First Name"
              fullWidth
              autoComplete="given-name"
              variant="standard"
              defaultValue={address.firstName ?? ""}
              error={address.firstName === ""}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              required
              id="lastName"
              name="lastName"
              label="Last Name"
              fullWidth
              autoComplete="family-name"
              variant="standard"
              defaultValue={address.lastName ?? ""}
              error={address.lastName === ""}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              required
              id="address1"
              name="address1"
              label="Address Line 1"
              fullWidth
              variant="standard"
              defaultValue={address.address1 ?? ""}
              error={address.address1 === ""}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              id="address2"
              name="address2"
              label="Address Line 2"
              fullWidth
              variant="standard"
              defaultValue={address.address2 ?? ""}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              required
              id="city"
              name="city"
              label="City"
              fullWidth
              variant="standard"
              defaultValue={address.city ?? ""}
              error={address.city === ""}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              id="zipCode"
              name="zipCode"
              label="Zip Code/Postal Code"
              fullWidth
              variant="standard"
              defaultValue={address.zipCode ?? ""}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              required
              id="country"
              name="country"
              label="Country"
              fullWidth
              variant="standard"
              defaultValue={address.country ?? ""}
              error={address.country === ""}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              required
              id="phoneNumber"
              name="phoneNumber"
              label="Phone Number"
              fullWidth
              variant="standard"
              defaultValue={address.phoneNumber ?? ""}
              error={address.phoneNumber === ""}
            />
          </Grid>
        </Grid>
      </Box>
    </>
  );
}

import React, { useState, useEffect } from "react";
import { Link, useNavigate, useSearchParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import {
  fetchAllCategories,
  selectAllCategories,
  selectCategoriesLoading,
  selectCategoriesError,
  setSelectedCategory,
  selectSelectedCategory,
} from "../feature/categories-slice";
import { selectAllProducts } from "../feature/products-slice";
import { getItemCount } from "../utils";
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  TextField,
  Select,
  MenuItem,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Box,
  useTheme,
  useMediaQuery,
  Menu,
  Badge,
  CircularProgress,
} from "@mui/material";
import {
  ShoppingCart,
  Search,
  Menu as MenuIcon,
  Close,
  Person,
  Receipt,
  KeyboardArrowDown,
} from "@mui/icons-material";
import Autocomplete from "@mui/material/Autocomplete";
import { styled } from "@mui/system";

// Styled Components
const HeaderContainer = styled(AppBar)(({ theme }) => ({
  backgroundColor: theme.palette.background.paper,
  boxShadow: theme.shadows[1],
}));

const Logo = styled(Typography)(({ theme }) => ({
  color: theme.palette.primary.main,
  fontWeight: 700,
  textDecoration: "none",
  marginRight: theme.spacing(2),
  cursor: "pointer",
  "&:hover": {
    color: theme.palette.primary.dark,
  },
}));

const NavContainer = styled(Box)(({ theme }) => ({
  display: "flex",
  alignItems: "center",
  width: "100%",
  justifyContent: "space-between",
  gap: theme.spacing(2),
}));

const SearchContainer = styled(Box)(({ theme }) => ({
  display: "flex",
  alignItems: "center",
  flex: 1,
  maxWidth: 800,
  margin: "0 auto",
  gap: theme.spacing(1),
}));

const CategorySelect = styled(Select)(({ theme }) => ({
  minWidth: 200,
  backgroundColor: theme.palette.background.paper,
  "& .MuiSelect-select": {
    paddingTop: 8,
    paddingBottom: 8,
  },
}));

const ActionButtons = styled(Box)(({ theme }) => ({
  display: "flex",
  alignItems: "center",
  gap: theme.spacing(2),
  marginLeft: "auto",
}));

const StyledBadge = styled(Badge)(({ theme }) => ({
  "& .MuiBadge-badge": {
    backgroundColor: theme.palette.primary.main,
    color: theme.palette.primary.contrastText,
  },
}));

const ProfileButton = styled(Button)(({ theme }) => ({
  textTransform: "none",
  color: theme.palette.text.primary,
  minWidth: 130,
  justifyContent: "flex-start",
  "&:hover": {
    backgroundColor: theme.palette.action.hover,
  },
}));

const DrawerContent = styled(Box)(({ theme }) => ({
  width: 280,
  padding: theme.spacing(2),
  height: "100%",
  display: "flex",
  flexDirection: "column",
}));

const MobileSearchContainer = styled(Box)(({ theme }) => ({
  padding: theme.spacing(2),
  borderBottom: `1px solid ${theme.palette.divider}`,
}));

const Header = ({ user, signOut }) => {
  // State
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);
  const [profileAnchorEl, setProfileAnchorEl] = useState(null);
  const [searchValue, setSearchValue] = useState("");

  // Hooks
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("md"));
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [searchParams] = useSearchParams();

  // Redux Selectors
  const categories = useSelector(selectAllCategories);
  const categoriesLoading = useSelector(selectCategoriesLoading);
  const categoriesError = useSelector(selectCategoriesError);
  const selectedCategory = useSelector(selectSelectedCategory);
  const products = useSelector(selectAllProducts); // Updated selector
  const cartItems = useSelector((state) => state.cart?.value || []); // Updated cart selector path
  const cartItemCount = getItemCount(cartItems);

  // Debugging
  console.log("Cart Items:", cartItems);
  console.log("Cart Item Count:", cartItemCount);

  // Effects
  useEffect(() => {
    dispatch(fetchAllCategories());
  }, [dispatch]);

  useEffect(() => {
    const category = searchParams.get("category");
    const searchTerm = searchParams.get("searchTerm");

    if (category) {
      dispatch(setSelectedCategory(category));
    }

    setSearchValue(searchTerm || "");
  }, [searchParams, dispatch]);

  // Handlers
  const handleCategoryChange = (event) => {
    const categoryId = event.target.value;
    dispatch(setSelectedCategory(categoryId));

    if (categoryId === "all") {
      navigate("/");
    } else {
      navigate(`/category/${categoryId}`);
    }
  };

  const handleSearch = (value) => {
    const searchQuery = new URLSearchParams();
    if (selectedCategory && selectedCategory !== "all") {
      searchQuery.set("category", selectedCategory);
    }
    if (value) {
      searchQuery.set("searchTerm", value.toLowerCase());
    }
    navigate(`/?${searchQuery.toString()}`);

    // Scroll to products section with a slight delay to ensure DOM update
    setTimeout(() => {
      const productsSection = document.getElementById("products");
      if (productsSection) {
        productsSection.scrollIntoView({ behavior: "smooth" });
      }
    }, 100);
  };

  const handleProfileClick = (event) => {
    setProfileAnchorEl(event.currentTarget);
  };

  const handleProfileClose = () => {
    setProfileAnchorEl(null);
  };

  const handleLogout = async () => {
    await signOut();
    handleProfileClose();
    navigate("/login");
  };

  const handleMyOrders = () => {
    handleProfileClose();
    navigate("/orders");
  };

  // Render Functions
  const renderSearch = () => (
    <SearchContainer>
      <CategorySelect
        value={selectedCategory || "all"}
        onChange={handleCategoryChange}
        size="small"
        displayEmpty
        disabled={categoriesLoading}
        MenuProps={{
          PaperProps: {
            style: {
              maxHeight: 400,
            },
          },
        }}
      >
        <MenuItem value="all">All Categories</MenuItem>
        {categories.map((category) => (
          <MenuItem
            key={category.id}
            value={category.id}
            sx={{
              textTransform: "capitalize",
              minHeight: "48px",
            }}
          >
            {category.name} ({category.productCount})
          </MenuItem>
        ))}
      </CategorySelect>

      {categoriesLoading ? (
        <CircularProgress size={24} />
      ) : (
        <Autocomplete
          freeSolo
          size="small"
          options={products.map((product) => product.name)}
          value={searchValue}
          onChange={(_, newValue) => {
            setSearchValue(newValue);
            handleSearch(newValue);
          }}
          renderInput={(params) => (
            <TextField
              {...params}
              placeholder="Search products..."
              fullWidth
              size="small"
              error={Boolean(categoriesError)}
              helperText={categoriesError}
            />
          )}
          sx={{ flex: 1 }}
        />
      )}

      <IconButton
        color="primary"
        onClick={() => handleSearch(searchValue)}
        disabled={categoriesLoading}
      >
        <Search />
      </IconButton>
    </SearchContainer>
  );

  const renderProfileMenu = () => (
    <Menu
      anchorEl={profileAnchorEl}
      open={Boolean(profileAnchorEl)}
      onClose={handleProfileClose}
      anchorOrigin={{
        vertical: "bottom",
        horizontal: "right",
      }}
      transformOrigin={{
        vertical: "top",
        horizontal: "right",
      }}
    >
      <MenuItem onClick={handleMyOrders}>
        <ListItemIcon>
          <Receipt fontSize="small" />
        </ListItemIcon>
        <ListItemText primary="My Orders" />
      </MenuItem>
      <MenuItem onClick={handleLogout}>
        <ListItemIcon>
          <Close fontSize="small" />
        </ListItemIcon>
        <ListItemText primary="Logout" />
      </MenuItem>
    </Menu>
  );

  return (
    <HeaderContainer position="sticky">
      <Toolbar>
        <NavContainer>
          <Logo variant="h6" component={Link} to="/">
            Lumi
          </Logo>

          {!isMobile && renderSearch()}

          {!isMobile && (
            <ActionButtons>
              <IconButton
                component={Link}
                to="/cart"
                color="inherit"
                sx={{
                  "&:hover": {
                    backgroundColor: "action.hover",
                  },
                }}
              >
                <StyledBadge badgeContent={cartItemCount} color="primary">
                  <ShoppingCart />
                </StyledBadge>
              </IconButton>

              {user ? (
                <>
                  <ProfileButton
                    onClick={handleProfileClick}
                    endIcon={<KeyboardArrowDown />}
                  >
                    <Person sx={{ mr: 1 }} />
                    {user.displayName || user.email}
                  </ProfileButton>
                  {renderProfileMenu()}
                </>
              ) : (
                <Button
                  component={Link}
                  to="/login"
                  color="primary"
                  variant="contained"
                >
                  Login
                </Button>
              )}
            </ActionButtons>
          )}

          {isMobile && (
            <IconButton
              edge="end"
              color="inherit"
              onClick={() => setIsDrawerOpen(true)}
            >
              <MenuIcon />
            </IconButton>
          )}
        </NavContainer>

        <Drawer
          anchor="right"
          open={isDrawerOpen}
          onClose={() => setIsDrawerOpen(false)}
        >
          <DrawerContent>
            <MobileSearchContainer>{renderSearch()}</MobileSearchContainer>

            <List>
              <ListItem
                button
                component={Link}
                to="/cart"
                onClick={() => setIsDrawerOpen(false)}
              >
                <ListItemIcon>
                  <ShoppingCart />
                </ListItemIcon>
                <ListItemText
                  primary="Cart"
                  secondary={`${cartItemCount} items`}
                />
              </ListItem>

              {user ? (
                <>
                  <ListItem button onClick={handleMyOrders}>
                    <ListItemIcon>
                      <Receipt />
                    </ListItemIcon>
                    <ListItemText primary="My Orders" />
                  </ListItem>
                  <ListItem button onClick={handleLogout}>
                    <ListItemIcon>
                      <Close />
                    </ListItemIcon>
                    <ListItemText primary="Logout" />
                  </ListItem>
                </>
              ) : (
                <ListItem
                  button
                  component={Link}
                  to="/login"
                  onClick={() => setIsDrawerOpen(false)}
                >
                  <ListItemIcon>
                    <Person />
                  </ListItemIcon>
                  <ListItemText primary="Login" />
                </ListItem>
              )}
            </List>
          </DrawerContent>
        </Drawer>
      </Toolbar>
    </HeaderContainer>
  );
};

export default Header;

import React from "react";
import { ThemeProvider, createTheme } from "@mui/material";
import { CssBaseline } from "@mui/material";
import Header from "./Header";
import { Outlet, useLocation } from "react-router-dom";
import { useAuth } from "../firebase/Auth"; // Make sure this path is correct

const theme = createTheme({
  palette: {
    mode: "dark",
    primary: {
      main: "#f5f5dc", // Beige
    },
    secondary: {
      main: "#d2b48c", // Tan (complementary to beige)
    },
    background: {
      default: "#1c1c1d", // Dark background
      paper: "#2c2c2d", // Slightly lighter dark background for paper elements
    },
    text: {
      primary: "#ffffff", // White text
      secondary: "#c0c0c0", // Light Gray text
    },
  },
  typography: {
    fontFamily: "'Quicksand', Arial, sans-serif",
  },
  shape: {
    borderRadius: 8,
  },
});

export default function Layout() {
  const { user, signOut } = useAuth();
  const location = useLocation();

  const isAuthPage =
    location.pathname === "/login" || location.pathname === "/register";

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      {!isAuthPage && <Header user={user} signOut={signOut} />}
      <main>
        <Outlet />
      </main>
      {!isAuthPage && <footer></footer>}
    </ThemeProvider>
  );
}

import React from "react";
import Typography from "@mui/material/Typography";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import Grid from "@mui/material/Grid";
import { useSelector } from "react-redux";

export default function ReviewForm() {
  const cart = useSelector((state) => state.cart.value);
  const address = useSelector((state) => state.checkout?.address);
  const promocodeDiscount = useSelector((state) => state.cart?.promocodeDiscount) || 0;
  const shippingCost = 300;

  // Calculate totals
  const subtotal = cart.reduce(
    (sum, item) => sum + item.product.price * item.quantity,
    0
  );
  const discountAmount = (subtotal * (promocodeDiscount / 100));
  const totalWithDiscount = subtotal - discountAmount + shippingCost;

  const addressFields = [
    `${address.firstName} ${address.lastName}`,
    address.address1,
    address.address2,
    address.city,
    address.zipCode,
    address.country,
    address.phoneNumber,
  ].filter(Boolean);

  return (
    <>
      <Typography variant="h6" gutterBottom>
        Order Summary
      </Typography>
      <List disablePadding>
        {cart.map(({ product, quantity, color, size }) => (
          <ListItem key={product.id + (size || "") + (color || "")} sx={{ py: 1, px: 0 }}>
            <ListItemText
              primary={product.name}
              secondary={`Color: ${color || 'N/A'} | Size: ${size || 'N/A'}`}
            />
            <Typography variant="body2">
              {quantity} x PKR {product.price.toFixed(2)} = PKR {(quantity * product.price).toFixed(2)}
            </Typography>
          </ListItem>
        ))}

        <ListItem sx={{ py: 1, px: 0 }}>
          <ListItemText primary="Subtotal" />
          <Typography variant="body1">
            PKR {subtotal.toFixed(2)}
          </Typography>
        </ListItem>

        {promocodeDiscount > 0 && (
          <ListItem sx={{ py: 1, px: 0 }}>
            <ListItemText primary={`Discount (${promocodeDiscount}%)`} />
            <Typography variant="body1" color="error">
              - PKR {discountAmount.toFixed(2)}
            </Typography>
          </ListItem>
        )}

        <ListItem sx={{ py: 1, px: 0 }}>
          <ListItemText primary="Shipping Cost" />
          <Typography variant="body1">
            PKR {shippingCost.toFixed(2)}
          </Typography>
        </ListItem>

        <ListItem sx={{ py: 1, px: 0 }}>
          <ListItemText primary="Total" />
          <Typography variant="subtitle1" sx={{ fontWeight: 700 }}>
            PKR {totalWithDiscount.toFixed(2)}
          </Typography>
        </ListItem>
      </List>

      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
          <Typography variant="h6" gutterBottom sx={{ mt: 2 }}>
            Shipping Address
          </Typography>
          <Typography gutterBottom>{addressFields.join(', ')}</Typography>
        </Grid>
      </Grid>
    </>
  );
}

import React from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { useAuth } from './Auth';
import CircularProgress from "@mui/material/CircularProgress"; // Import CircularProgress


export const AdminRoute = ({ children }) => {
  const { user, userRole, loading } = useAuth();
  const location = useLocation();

  if (loading) {
    return <CircularProgress />;
  }

  if (!user || userRole !== "admin") {
    // Redirect them to the login page, but save the current location they were
    // trying to go to when they were redirected

    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  return children;
};

import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";
import {
  createUserWithEmailAndPassword,
  getAuth,
  onAuthStateChanged,
  signInWithEmailAndPassword,
  signOut,
  updateProfile,
  sendEmailVerification,
} from "firebase/auth";
import { getFirestore, doc, setDoc, getDoc } from "firebase/firestore";
import { useContext, useEffect, useState, createContext } from "react";
import { sendPasswordResetEmail } from "firebase/auth";
import { useSelector } from 'react-redux';


// Your existing Firebase config remains the same
const firebaseConfig = {
  apiKey: "AIzaSyDlHQYH7qv81iO-04pFOP47Efe8kms5NAs",
  authDomain: "testingestore-5a878.firebaseapp.com",
  projectId: "testingestore-5a878",
  storageBucket: "testingestore-5a878.appspot.com",
  messagingSenderId: "866805788601",
  appId: "1:866805788601:web:4864c99285d15028d59068",
  measurementId: "G-2PC7TLW2GJ",
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);
const auth = getAuth(app);
const db = getFirestore(app);

const AuthContext = createContext(null);

const AuthProvider = ({ children }) => {
  const auth = useProvideAuth();
  return <AuthContext.Provider value={auth}>{children}</AuthContext.Provider>;
};

export const useAuth = () => useContext(AuthContext);
export { db };

function useProvideAuth() {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [verificationEmailSent, setVerificationEmailSent] = useState(false);
  const currentCart = useSelector(state => state.cart.value);
  const [userRole, setUserRole] = useState(null);

  const checkUserRole = async (uid) => {
    try {
      const roleDoc = await getDoc(doc(db, "roles", uid));
      return roleDoc.exists() ? roleDoc.data().role : "user";
    } catch (error) {
      console.error("Error checking user role:", error);
      return "user";
    }
  };

  const resetPassword = async (email) => {
    try {
      await sendPasswordResetEmail(auth, email);
      setError(null);
      return true;
    } catch (error) {
      setError(getErrorMessage(error.code));
      throw error;
    }
  };

  const getErrorMessage = (errorCode) => {
    switch (errorCode) {
      case "auth/email-already-in-use":
        return "The email address is already in use by another account.";
      case "auth/invalid-email":
        return "The email address is not valid.";
      case "auth/operation-not-allowed":
        return "Email/password accounts are not enabled.";
      case "auth/weak-password":
        return "The password is too weak.";
      case "auth/user-disabled":
        return "The user account has been disabled.";
      case "auth/user-not-found":
        return "There is no user record corresponding to this identifier.";
      case "auth/wrong-password":
        return "The password is invalid.";
      default:
        return "An error occurred. Please try again.";
    }
  };

  const signUp = async (email, password, displayName) => {
    try {
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        email,
        password
      );
      const { user } = userCredential;

      // Update the user's profile
      await updateProfile(user, { displayName });

      // Send verification email
      await sendEmailVerification(user);
      setVerificationEmailSent(true);

      // Add user data to Firestore
      await setDoc(doc(db, "users", user.uid), {
        displayName,
        email,
        cart: currentCart,
        emailVerified: false,
      });

      setUser(user);
      setError(null);
      return user;
    } catch (error) {
      setError(getErrorMessage(error.code));
      throw error;
    }
  };

  const signIn = async (email, password) => {
    try {
      const userCredential = await signInWithEmailAndPassword(
        auth,
        email,
        password
      );
      const { user } = userCredential;

      if (!user.emailVerified) {
        throw new Error("Please verify your email before signing in.");
      }
      const role = await checkUserRole(user.uid);
      // Update the user's cart in Firestore with current cart state
      await setDoc(doc(db, "users", user.uid), {
        cart: currentCart,
        role: role // Store role in user document
      }, { merge: true });

      const userData = await getUserData(user.uid);
      setUser({ ...user, ...userData, role });
      setUserRole(role);
      setError(null);
      return { ...user, role };
    } catch (error) {
      if (error.message === "Please verify your email before signing in.") {
        setError(error.message);
      } else {
        setError(getErrorMessage(error.code));
      }
      throw error;
    }
  };

  const resendVerificationEmail = async () => {
    if (user && !user.emailVerified) {
      try {
        await sendEmailVerification(user);
        setVerificationEmailSent(true);
        return true;
      } catch (error) {
        setError(getErrorMessage(error.code));
        throw error;
      }
    }
    return false;
  };

  const signOutUser = async () => {
    try {
      await signOut(auth);
      setUser(null);
      setError(null);
    } catch (error) {
      setError(getErrorMessage(error.code));
      throw error;
    }
  };

  const getUserData = async (uid) => {
    try {
      const userDoc = await getDoc(doc(db, "users", uid));
      if (userDoc.exists()) {
        return userDoc.data();
      } else {
        throw new Error("User document does not exist");
      }
    } catch (error) {
      setError(getErrorMessage(error.code));
      throw error;
    }
  };

  const updateUserData = async (uid, data) => {
    try {
      await setDoc(doc(db, "users", uid), data, { merge: true });
    } catch (error) {
      setError(getErrorMessage(error.code));
      throw error;
    }
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      if (firebaseUser) {
        try {
          const userData = await getUserData(firebaseUser.uid);
          setUser({ ...firebaseUser, ...userData });

          // Update email verification status in Firestore
          if (firebaseUser.emailVerified) {
            await updateUserData(firebaseUser.uid, {
              emailVerified: true,
            });
          }
        } catch (error) {
          console.error("Error fetching user data:", error);
          setUser(firebaseUser);
        }
      } else {
        setUser(null);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  return {
    user,
    error,
    loading,
    verificationEmailSent,
    signIn,
    signUp,
    signOut: signOutUser,
    getUserData,
    updateUserData,
    resendVerificationEmail,
    resetPassword,
    checkUserRole,
    userRole,
  };
}

export default AuthProvider;

import React from "react";
import ReactDOM from "react-dom/client";
import App from "./App";
import "@fontsource/roboto/300.css";
import "@fontsource/roboto/400.css";
import "@fontsource/roboto/500.css";
import "@fontsource/roboto/700.css";

import "./index.css";

ReactDOM.createRoot(document.getElementById("root")).render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

import React, { useState, useEffect } from "react";
import {
  collection,
  getDocs,
  updateDoc,
  doc,
  deleteDoc,
  addDoc,
} from "firebase/firestore";
import { getStorage, ref, uploadBytes, getDownloadURL } from "firebase/storage";
import {
  Container,
  Grid,
  Typography,
  Tabs,
  Tab,
  Box,
  Paper,
  TableContainer,
  Table,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
  Select,
  MenuItem,
  IconButton,
  Collapse,
  Chip,
  Button,
  TextField,
  InputAdornment,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
  Snackbar,
  LinearProgress,
  FormControl,
  FormControlLabel,
  InputLabel,
  Checkbox,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Switch,
} from "@mui/material";
import {
  KeyboardArrowDown,
  KeyboardArrowUp,
  Search as SearchIcon,
  FilterList as FilterIcon,
  Delete,
  Edit,
  Add,
  SaveAlt,
  Cancel,
  Image as ImageIcon,
} from "@mui/icons-material";
import { db } from "../firebase/Auth";

const OrderStatus = {
  PENDING: "pending",
  CONFIRMED: "confirmed",
  SHIPPED: "shipped",
  DELIVERED: "delivered",
  CANCELLED: "cancelled",
};

const statusColors = {
  [OrderStatus.PENDING]: "warning",
  [OrderStatus.CONFIRMED]: "info",
  [OrderStatus.SHIPPED]: "primary",
  [OrderStatus.DELIVERED]: "success",
  [OrderStatus.CANCELLED]: "error",
};

const categories = ["Acid Wash", "Graphic Hoods", "Totes", "Thirfts"];

const AdminPage = () => {
  const [tabValue, setTabValue] = useState(0);
  const [orders, setOrders] = useState([]);
  const [expandedOrder, setExpandedOrder] = useState(null);
  const [statusFilter, setStatusFilter] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [loading, setLoading] = useState(false);
  const [products, setProducts] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: "",
    severity: "success",
  });
  const [editMode, setEditMode] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [images, setImages] = useState([]);
  const [imageURLs, setImageURLs] = useState([]);

  const [formData, setFormData] = useState({
    name: "",
    category: "",
    description: "",
    price: "",
    stock: "",
    color: "",
    size: [],
    features: "",
    status: "available",
  });

  const sizeOptions = ["XS", "S", "M", "L", "XL", "XXL"];

  const [promocodes, setPromocodes] = useState([]);
  const [openPromocodeDialog, setOpenPromocodeDialog] = useState(false);
  const [promocodeFormData, setPromocodeFormData] = useState({
    code: "",
    discount: "",
    valid: true,
  });

  const [editingPromocode, setEditingPromocode] = useState(null);

  useEffect(() => {
    if (tabValue === 0) {
      fetchProducts();
    } else if (tabValue === 1) {
      fetchOrders();
    } else if (tabValue === 2) {
      fetchPromocodes();
    }
  }, [tabValue]);

  useEffect(() => {
    if (images.length > 0) {
      const newImageURLs = images.map((image) => URL.createObjectURL(image));
      setImageURLs(newImageURLs);
      return () => newImageURLs.forEach((url) => URL.revokeObjectURL(url));
    }
  }, [images]);

  const fetchProducts = async () => {
    setLoading(true);
    try {
      const querySnapshot = await getDocs(collection(db, "products"));
      setProducts(
        querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }))
      );
    } catch (error) {
      showSnackbar("Error fetching products", "error");
    }
    setLoading(false);
  };

  const fetchOrders = async () => {
    setLoading(true);
    try {
      const querySnapshot = await getDocs(collection(db, "orders"));
      const ordersData = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
        orderDate: doc.data().orderDate?.toDate(),
      }));
      setOrders(ordersData.sort((a, b) => b.orderDate - a.orderDate));
    } catch (error) {
      showSnackbar("Error fetching orders", "error");
    }
    setLoading(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSizeChange = (size) => {
    setFormData((prev) => ({
      ...prev,
      size: prev.size.includes(size)
        ? prev.size.filter((s) => s !== size)
        : [...prev.size, size],
    }));
  };

  const handleImageChange = (e) => {
    const files = [...e.target.files].slice(0, 4);
    setImages(files);
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      await updateDoc(doc(db, "orders", orderId), {
        status: newStatus,
        updatedAt: new Date(),
      });
      await fetchOrders();
    } catch (error) {
      showSnackbar("Error updating order status", "error");
    }
  };

  const showSnackbar = (message, severity = "success") => {
    setSnackbar({ open: true, message, severity });
  };

  const resetForm = () => {
    setFormData({
      name: "",
      category: "",
      description: "",
      price: "",
      stock: "",
      color: "",
      size: [],
      features: "",
      status: "available",
    });
    setImages([]);
    setImageURLs([]);
    setSelectedProduct(null);
    setEditMode(false);
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const storage = getStorage();
      let imageUrls = [];
      setUploadProgress(0);

      if (images.length > 0) {
        for (let i = 0; i < images.length; i++) {
          const image = images[i];
          const imageRef = ref(storage, `products/${Date.now()}_${image.name}`);
          await uploadBytes(imageRef, image);
          const url = await getDownloadURL(imageRef);
          imageUrls.push(url);
          setUploadProgress(((i + 1) / images.length) * 100);
        }
      } else if (editMode && selectedProduct) {
        imageUrls = selectedProduct.imageUrl;
      }

      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        stock: parseInt(formData.stock),
        imageUrl: imageUrls,
        createdAt: editMode ? selectedProduct.createdAt : new Date(),
        updatedAt: new Date(),
      };

      if (editMode && selectedProduct) {
        await updateDoc(doc(db, "products", selectedProduct.id), productData);
        showSnackbar("Product updated successfully");
      } else {
        await addDoc(collection(db, "products"), productData);
        showSnackbar("Product added successfully");
      }

      resetForm();
      setOpenDialog(false);
      fetchProducts();
    } catch (error) {
      showSnackbar("Error saving product", "error");
    }
    setLoading(false);
  };

  const handleEditProduct = (product) => {
    setSelectedProduct(product);
    setFormData({
      name: product.name,
      category: product.category,
      description: product.description,
      price: product.price.toString(),
      stock: product.stock.toString(),
      color: product.color || "",
      size: product.size || [],
      features: product.features || "",
      status: product.status || "available",
    });
    setImageURLs(product.imageUrl || []);
    setEditMode(true);
    setOpenDialog(true);
  };

  const handleDeleteProduct = async (id) => {
    if (window.confirm("Are you sure you want to delete this product?")) {
      try {
        await deleteDoc(doc(db, "products", id));
        showSnackbar("Product deleted successfully");
        fetchProducts();
      } catch (error) {
        showSnackbar("Error deleting product", "error");
      }
    }
  };

  const calculateTotal = (items) => {
    if (!Array.isArray(items)) return 0;
    return (
      items.reduce((sum, item) => {
      const price = item?.price || 0;
      const quantity = item?.quantity || 0;
      return sum + price * quantity;
      }, 0) * 0.75 + 300
    );
  };

  const getUniqueCategories = (products) => {
    const categories = products.map((product) => product.category);
    return ["All", ...new Set(categories)];
  };

  const fetchPromocodes = async () => {
    setLoading(true);
    try {
      const querySnapshot = await getDocs(collection(db, "promocodes"));
      setPromocodes(
        querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }))
      );
    } catch (error) {
      showSnackbar("Error fetching promocodes", "error");
    }
    setLoading(false);
  };

  const handlePromocodeInputChange = (e) => {
    const { name, value, checked } = e.target;
    setPromocodeFormData((prev) => ({
      ...prev,
      [name]: name === "valid" ? checked : value,
    }));
  };

  const resetPromocodeForm = () => {
    setPromocodeFormData({
      code: "",
      discount: "",
      valid: true,
    });
    setEditingPromocode(null);
  };

  const handleAddPromocode = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const promocodeData = {
        ...promocodeFormData,
        discount: parseInt(promocodeFormData.discount),
        createdAt: editingPromocode ? editingPromocode.createdAt : new Date(),
        updatedAt: new Date(),
      };

      if (editingPromocode) {
        await updateDoc(
          doc(db, "promocodes", editingPromocode.id),
          promocodeData
        );
        showSnackbar("Promocode updated successfully");
      } else {
        await addDoc(collection(db, "promocodes"), promocodeData);
        showSnackbar("Promocode added successfully");
      }

      resetPromocodeForm();
      setOpenPromocodeDialog(false);
      fetchPromocodes();
    } catch (error) {
      showSnackbar("Error saving promocode", "error");
    }
    setLoading(false);
  };

  const handleEditPromocode = (promocode) => {
    setEditingPromocode(promocode);
    setPromocodeFormData({
      code: promocode.code,
      discount: promocode.discount.toString(),
      valid: promocode.valid,
    });
    setOpenPromocodeDialog(true);
  };

  const handleDeletePromocode = async (id) => {
    if (window.confirm("Are you sure you want to delete this promocode?")) {
      try {
        await deleteDoc(doc(db, "promocodes", id));
        showSnackbar("Promocode deleted successfully");
        fetchPromocodes();
      } catch (error) {
        showSnackbar("Error deleting promocode", "error");
      }
    }
  };

  const handleTogglePromocodeStatus = async (promocode) => {
    try {
      await updateDoc(doc(db, "promocodes", promocode.id), {
        valid: !promocode.valid,
        updatedAt: new Date(),
      });
      fetchPromocodes();
    } catch (error) {
      showSnackbar("Error updating promocode status", "error");
    }
  };

  const filteredProducts =
    selectedCategory === "All"
      ? products
      : products.filter((product) => product.category === selectedCategory);

  const filteredOrders = orders.filter((order) => {
    const matchesStatus =
      statusFilter === "all" || order.status === statusFilter;
    const searchLower = searchQuery.toLowerCase();
    const matchesSearch =
      order.id.toLowerCase().includes(searchLower) ||
      order.shippingAddress?.fullName?.toLowerCase().includes(searchLower) ||
      order.customerEmail?.toLowerCase().includes(searchLower);
    return matchesStatus && matchesSearch;
  });

  const OrderRow = ({ order }) => {
    const isExpanded = expandedOrder === order.id;
    const orderItems = Array.isArray(order.items) ? order.items : [];

    return (
      <>
        <TableRow sx={{ "& > *": { borderBottom: "unset" } }}>
          <TableCell>
            <IconButton
              size="small"
              onClick={() => setExpandedOrder(isExpanded ? null : order.id)}
            >
              {isExpanded ? <KeyboardArrowUp /> : <KeyboardArrowDown />}
            </IconButton>
          </TableCell>
          <TableCell>{order.id.slice(-8)}</TableCell>
          <TableCell>{order.orderDate?.toLocaleString()}</TableCell>
          <TableCell>{order.shippingAddress?.fullName}</TableCell>
          <TableCell>
            <Select
              value={order.status || "pending"}
              onChange={(e) => handleStatusChange(order.id, e.target.value)}
              size="small"
            >
              {Object.values(OrderStatus).map((status) => (
                <MenuItem key={status} value={status}>
                  <Chip
                    label={status}
                    size="small"
                    color={statusColors[status]}
                  />
                </MenuItem>
              ))}
            </Select>
          </TableCell>
          <TableCell>PKR {calculateTotal(orderItems).toFixed(2)}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
            <Collapse in={isExpanded} timeout="auto" unmountOnExit>
              <Box sx={{ margin: 1 }}>
                <Typography variant="h6" gutterBottom component="div">
                  Order Details
                </Typography>
                <Grid container spacing={2}>
                  <Grid item xs={12} md={6}>
                    <Typography variant="subtitle2">
                      Shipping Address:
                    </Typography>
                    <Typography variant="body2">
                      {order.shippingAddress?.address1}
                      <br />
                      {order.shippingAddress?.street}
                      <br />
                      {order.shippingAddress?.city},{" "}
                      {order.shippingAddress?.state}{" "}
                      {order.shippingAddress?.zip}
                      <br />
                      {order.shippingAddress?.country}
                    </Typography>
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <Typography variant="subtitle2">
                      Customer Contact:
                    </Typography>
                    <Typography variant="body2">
                      Email: {order.email}
                      <br />
                      Phone: {order.shippingAddress?.phoneNumber || "N/A"}
                      <br />
                      Name:{" "}
                      {order.shippingAddress?.firstName +
                        " " +
                        order.shippingAddress?.lastName || "N/A"}
                    </Typography>
                  </Grid>
                </Grid>
                <Table size="small" sx={{ mt: 2 }}>
                  <TableHead>
                    <TableRow>
                      <TableCell>Product</TableCell>
                      <TableCell>Size</TableCell>
                      <TableCell align="right">Quantity</TableCell>
                      <TableCell align="right">Price</TableCell>
                      <TableCell align="right">Total</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {orderItems.map((item, index) => (
                      <TableRow key={index}>
                        <TableCell>{item.name}</TableCell>
                        <TableCell>{item.size || "N/A"}</TableCell>
                        <TableCell align="right">{item.quantity}</TableCell>
                        <TableCell align="right">
                          PKR {item.price.toFixed(2)}
                        </TableCell>
                        <TableCell align="right">
                          PKR {(item.price * item.quantity).toFixed(2)}
                        </TableCell>
                      </TableRow>
                    ))}
                    <TableRow>
                      <TableCell colSpan={3} />
                      <TableCell align="right">Shipping:</TableCell>
                      <TableCell align="right">PKR 300.00</TableCell>
                    </TableRow>
                    <TableRow>
                      <TableCell colSpan={3} />
                      <TableCell align="right">
                        <strong>Total:</strong>
                      </TableCell>
                      <TableCell align="right">
                        <strong>
                          PKR {calculateTotal(orderItems).toFixed(2)}
                        </strong>
                      </TableCell>
                    </TableRow>
                  </TableBody>
                </Table>
              </Box>
            </Collapse>
          </TableCell>
        </TableRow>
      </>
    );
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Tabs
        value={tabValue}
        onChange={(e, newValue) => setTabValue(newValue)}
        sx={{ mb: 4 }}
      >
        <Tab label="Products" />
        <Tab label="Orders" />
        <Tab label="Promocodes" />
      </Tabs>

      {tabValue === 0 ? (
        <>
          <Box sx={{ display: "flex", justifyContent: "space-between", mb: 4 }}>
            <Typography variant="h4" component="h1" gutterBottom>
              Product Management
            </Typography>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={() => {
                resetForm();
                setOpenDialog(true);
              }}
            >
              Add New Product
            </Button>
          </Box>

          <Box sx={{ display: "flex", justifyContent: "space-between", mb: 4 }}>
            <FormControl sx={{ minWidth: 200 }}>
              <InputLabel>Filter by Category</InputLabel>
              <Select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                label="Filter by Category"
              >
                {getUniqueCategories(products).map((category) => (
                  <MenuItem key={category} value={category}>
                    {category}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Box>

          {loading && <LinearProgress />}

          <Grid container spacing={3}>
            {filteredProducts.map((product) => (
              <Grid item xs={12} sm={6} md={4} key={product.id}>
                <Card>
                  <CardMedia
                    component="img"
                    height="200"
                    image={product.imageUrl?.[0] || "/placeholder.png"}
                    alt={product.name}
                    sx={{ objectFit: "contain", bgcolor: "grey.100" }}
                  />
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      {product.name}
                    </Typography>
                    <Box sx={{ display: "flex", gap: 1, mb: 1 }}>
                      <Chip label={product.category} size="small" />
                      <Chip
                        label={`${product.stock} in stock`}
                        size="small"
                        color={product.stock > 0 ? "success" : "error"}
                      />
                    </Box>
                    <Typography variant="body2" color="text.secondary" noWrap>
                      {product.description}
                    </Typography>
                    <Typography variant="h6" color="primary" sx={{ mt: 1 }}>
                      PKR {Number(product.price).toFixed(0)}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button
                      size="small"
                      startIcon={<Edit />}
                      onClick={() => handleEditProduct(product)}
                    >
                      Edit
                    </Button>
                    <Button
                      size="small"
                      color="error"
                      startIcon={<Delete />}
                      onClick={() => handleDeleteProduct(product.id)}
                    >
                      Delete
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </>
      ) : tabValue === 1 ? (
        <Box>
          <Box sx={{ mb: 3, display: "flex", gap: 2, alignItems: "center" }}>
            <TextField
              placeholder="Search orders..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
              size="small"
            />
            <Select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              size="small"
              startAdornment={
                <InputAdornment position="start">
                  <FilterIcon />
                </InputAdornment>
              }
            >
              <MenuItem value="all">All Status</MenuItem>
              {Object.values(OrderStatus).map((status) => (
                <MenuItem key={status} value={status}>
                  {status}
                </MenuItem>
              ))}
            </Select>
          </Box>

          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>Order ID</TableCell>
                  <TableCell>Date</TableCell>
                  <TableCell>Customer</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Total</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredOrders.map((order) => (
                  <OrderRow key={order.id} order={order} />
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Box>
      ) : (
        <Box>
          <Box sx={{ display: "flex", justifyContent: "space-between", mb: 4 }}>
            <Typography variant="h4" component="h1" gutterBottom>
              Promocode Management
            </Typography>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={() => {
                resetPromocodeForm();
                setOpenPromocodeDialog(true);
              }}
            >
              Add New Promocode
            </Button>
          </Box>

          {loading && <LinearProgress />}

          <TableContainer component={Paper}>
            <Table>
              <TableHead>
