const CategoryPage = () => {
  const { categoryId } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down('sm'));

  const products = useSelector(selectAllProducts);
  const loading = useSelector(selectProductsLoading);
  const categories = useSelector(selectAllCategories);

  const category = categories.find(cat => cat.id === categoryId);
  const categoryProducts = products.filter(product => product.category === category?.name);

  // Reset category selection when navigating to home
  const handleHomeClick = (e) => {
    e.preventDefault();
    dispatch(setSelectedCategory('all'));
    navigate('/');
  };

  // Scroll to top when category changes
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [categoryId]);

  if (!category) {
    return (
      <Container>
        <Typography variant="h4" align="center" sx={{ mt: 4 }}>
          Category not found
        </Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ py: isSmallScreen ? 4 : 8 }}>
      <Breadcrumbs 
        separator={<NavigateNext fontSize="small" />} 
        sx={{ mb: 4 }}
      >
        <Link 
          color="inherit" 
          href="/"
          onClick={handleHomeClick}
          sx={{ cursor: 'pointer' }}
        >
          Home
        </Link>
        <Typography color="text.primary" sx={{ textTransform: 'capitalize' }}>
          {category.name}
        </Typography>
      </Breadcrumbs>

      <Typography 
        variant={isSmallScreen ? 'h4' : 'h3'} 
        component="h1" 
        gutterBottom
        sx={{
          mb: 4,
          fontWeight: 700,
          textTransform: 'capitalize',
        }}
      >
        {category.name}
        <Typography 
          component="span" 
          color="text.secondary" 
          sx={{ ml: 2, fontSize: '0.7em' }}
        >
          ({categoryProducts.length} products)
        </Typography>
      </Typography>

      {loading ? (
        <Typography variant="h5" align="center">Loading products...</Typography>
      ) : categoryProducts.length === 0 ? (
        <Typography variant="h6" align="center">No products found in this category.</Typography>
      ) : (
        <Grid container spacing={isSmallScreen ? 3 : 4}>
          <AnimatePresence>
            {categoryProducts.map((product, index) => (
              <Grid item key={product.id} xs={6} sm={6} md={4} lg={3}>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: 20 }}
                  transition={{ duration: 0.3, delay: index * 0.1 }}
                >
