export default function ProductDetail() {
  const { productId } = useParams();
  const dispatch = useDispatch();
  const { currentProduct, loading } = useSelector((state) => state.products);
  const [selectedColor, setSelectedColor] = useState("");
  const [selectedSize, setSelectedSize] = useState("");
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState("");
  const [snackbarSeverity, setSnackbarSeverity] = useState("success");
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [sizeError, setSizeError] = useState(false);
  const [imageTransitioning, setImageTransitioning] = useState(false);

  useEffect(() => {
    dispatch(fetchProductById(productId));
  }, [dispatch, productId]);

  useEffect(() => {
    if (currentProduct) {
      setSelectedColor(currentProduct.colors?.[0] || "");
      setCurrentImageIndex(0);
      setSizeError(false);
      setSelectedSize("");
    }
  }, [currentProduct]);

  const handleAddToCart = () => {
    if (!selectedSize) {
      setSnackbarMessage("Please select a size before adding to cart");
      setSnackbarSeverity("error");
      setSnackbarOpen(true);
      setSizeError(true);
      return;
    }

    if (currentProduct) {
      dispatch(
        addToCart({
          product: currentProduct,
          quantity: 1,
          color: selectedColor,
          size: selectedSize,
        })
      );
      setSnackbarMessage(`${currentProduct.name} added to cart`);
      setSnackbarSeverity("success");
      setSnackbarOpen(true);
      setSizeError(false);
    }
  };

  const handleSnackbarClose = () => {
    setSnackbarOpen(false);
  };

  const handlePrevImage = () => {
    setImageTransitioning(true);
    setTimeout(() => {
      setCurrentImageIndex((prev) =>
        prev === 0 ? currentProduct.imageUrl.length - 1 : prev - 1
      );
      setImageTransitioning(false);
    }, 300);
  };

  const handleNextImage = () => {
    setImageTransitioning(true);
    setTimeout(() => {
      setCurrentImageIndex((prev) =>
        prev === currentProduct.imageUrl.length - 1 ? 0 : prev + 1
      );
      setImageTransitioning(false);
    }, 300);
  };

  if (loading) {
    return (
      <Container sx={{ py: 8, display: "flex", justifyContent: "center" }}>
        <CircularProgress />
      </Container>
    );
  }

  if (!currentProduct) {
    return <Typography variant="h5">Product not found</Typography>;
  }

  const {
    name,
    price,
    description,
    imageUrl = [],
    colors = [],
    size = [],
    gender,
    fit,
    category,
  } = currentProduct || {};

  const productDetails = getProductDetails(category);

  return (
    <Container maxWidth="lg" sx={{ py: 8 }}>
      <Grid container spacing={4}>
        <Grid item xs={12} md={6}>
          <Box sx={{ position: "relative", overflow: "hidden" }}>
            {imageUrl.length > 0 && (
              <>
                <img
                  src={imageUrl[currentImageIndex]}
                  alt={`${name} - View ${currentImageIndex + 1}`}
                  style={{
                    width: "100%",
                    height: "auto",
                    opacity: imageTransitioning ? 0 : 1,
                    transition: "opacity 0.3s ease-in-out",
                  }}
                />
                {imageUrl.length > 1 && (
                  <>
                    <IconButton
                      onClick={handlePrevImage}
                      sx={{
                        position: "absolute",
                        left: 8,
                        top: "50%",
                        transform: "translateY(-50%)",
                        bgcolor: "rgba(255, 255, 255, 0.8)",
                        "&:hover": { bgcolor: "rgba(255, 255, 255, 0.9)" },
                      }}
                    >
                      <ChevronLeft />
                    </IconButton>
                    <IconButton
                      onClick={handleNextImage}
                      sx={{
                        position: "absolute",
                        right: 8,
                        top: "50%",
                        transform: "translateY(-50%)",
                        bgcolor: "rgba(255, 255, 255, 0.8)",
                        "&:hover": { bgcolor: "rgba(255, 255, 255, 0.9)" },
                      }}
                    >
                      <ChevronRight />
                    </IconButton>
                  </>
                )}
              </>
            )}
          </Box>
          {imageUrl.length > 1 && (
            <Box
              sx={{ display: "flex", gap: 1, mt: 2, overflowX: "auto", py: 1 }}
            >
              {imageUrl.map((url, index) => (
                <Box
                  key={index}
                  onClick={() => {
                    setImageTransitioning(true);
                    setTimeout(() => {
                      setCurrentImageIndex(index);
                      setImageTransitioning(false);
                    }, 300);
                  }}
                  sx={{
                    width: 60,
                    height: 60,
                    flexShrink: 0,
                    cursor: "pointer",
                    border:
                      currentImageIndex === index
                        ? "2px solid #1976d2"
                        : "1px solid #ddd",
                    "&:hover": { opacity: 0.8 },
                    transition: "border-color 0.3s ease",
