                <Typography
                  variant="subtitle2"
                  sx={{ mb: 1, fontWeight: "bold" }}
                >
                  Features
                </Typography>
                <ul style={{ marginLeft: "20px", marginBottom: "16px" }}>
                  {productDetails.features.map((item, index) => (
                    <li key={index}>
                      <Typography variant="body2">{item}</Typography>
